        background-color: $black;
        padding: 8px 12px;
        border-radius: 30px;
        margin-right: 30px;

        li a {
          color: $gray;

          &:hover {
            color: $white;
          }
        }

        .lang--kor {
          margin-right: 20px;

          a {
            color: $white;
          }

          &::after {
            content: "";
            color: $white;
            width: 1px;
            height: 10px;
          }
        }
      }

      .sub-menu {
        cursor: pointer;

        .material-icons {
          font-size: 35px;
          ;
        }
      }
    }
  }

  main {
    margin-top: 100px;
  }

  // VIDEOS
  .videos {
    position: relative;

    .carousel {
      height: 35vw;
    }

    .carousel-cell {
      width: 100vw;
      height: auto;
    }

    video {
      display: block;
      width: 100%;
      height: 100%;
    }

    .progress {
      position: absolute;
      max-width: 1280px;
      width: 90%;
      z-index: 5;
      bottom: 50px;
      text-align: end;
      right: 0;
      left: 0;
      margin: 0 auto;

