        right: 0;

        .next-btn {
          cursor: pointer;
        }
      }

      .bar-wrap {
        margin-top: 35px;
        background-image: url("../images/progress_bar_bg.png");

        .bar {
          height: 3px;
          background-color: #fff;
        }
      }

      .status {
        margin-top: 10px;
        font-size: 13px;
        color: $light-gray;

        span {
          color: $gray;

        }
      }
    }
  }

  // WE MAKE GAMES BETTER
  .games {
    align-items: center;
    position: relative;
    padding-bottom: 200px;
    margin-top: 80px;

    .games__head {
      text-align: center;


      h1 {
        font-size: 75px;
        font-weight: 300;
        line-height: 1.3;
