sources.scss/main.scss:
$gray: rgb(153, 153, 153);
$light-gray: rgb(232, 232, 232);
$lime: rgb(202, 212, 1);
$black: rgb(34, 34, 34);
$news-gray: rgb(136, 136, 136);
$blue: rgb(1, 159, 232);
$white: rgb(255, 255, 255);

// temp
.recruite {
  img {
    width: 90vw;
  }
}

.inner {
  width: 1240px;
  margin: auto;
}

.a-btn {
  font-size: 28px;
  font-weight: 800;
  position: relative;
  height: min-content;
  transition: .15s;
  right: 0;

  &:hover {
    right: -10px;
  }

  &:before {
    content: '';
    position: absolute;
    width: 100%;
    height: 10px;
    background-color: $blue;
    top: 15px;
    left: 0;
    z-index: -1;
  }

  &:after {
    content: '';
    position: absolute;
    right: -30px;
    top: 10px;
    width: 17px;
    height: 8px;
    background-image: url('https://www.nexongames.co.kr/img/main/slide_next.png');
  }
}

.head {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  margin-bottom: 80px;

  h2 {
    font-size: 5vw;
    font-weight: 800;
  }
}



a {
  text-decoration: none;
  color: $black;
}

body {
  font-family: 'NexonLv2Gothic', sans-serif;
  box-sizing: border-box;

  // HEADER
  header {
    display: flex;
    position: fixed;
    justify-content: space-between;
    align-items: center;
    background-color: $white;
    padding: 0 10px 0 20px;
    height: 100px;
    top: 0;
    right: 0;
    left: 0;
    z-index: 999;

    .lang {
      display: flex;
      font-size: 11px;
      align-items: center;
      background-color: $black;
      padding: 8px 12px;
      border-radius: 30px;
      margin-right: 30px;
      li a {
        color: $gray;
        &:hover {
          color: $white;
        }
      }
      .lang--kor {
        margin-right: 20px;
        a {
          color: $white;
        }
        &::after {
          content: "";
          color: $white;
          width: 1px;
          height: 10px;
        }
      }
    }

    .menu {
      display: flex;
      align-items: center;

      .sub-menu {
        cursor: pointer;
        height: 35px;

        .material-icons {
          font-size: 35px;
          ;
        }
      }

      .sub-menu__extend {
        display: none;
        position: fixed;
        right: -75%;
        top: 0;
        height: 100vh;
        background-color: #fff;
        width: 75%;

        .sub-menu__head {
          display: flex;
          justify-content: end;
          padding: 15px;
        }
        .material-icons {
          font-weight: 900;
          color: gray;
          cursor: pointer;
        }

        a {
          height: 50px;
          display: block;
          padding: 0 15px;
          width: 100%;
        }
        .row--head {
          border-top: 1px solid #eee;
          font-size: 16px;
          line-height: 50px;

          &:last-child {
            border-bottom: 1px solid #eee;
          }

          > a {
            color: #3c6189;
            font-weight: 500;
          }

          .row__sub {
            display: none;
            li {
              background-color: #eee;
              font-size: 14px;
            }
          }

          .on {
            background-color: #f3f3f3;
          }
        }

        .lang {
          margin-right: 40px;

          a {
            height: auto;
            padding: 0;
          }
        }
      }
    }
  }

  main {
    margin-top: 100px;
  }

  // VIDEOS
  .videos {
    position: relative;

    .carousel {
      height: 35vw;
    }

    .carousel-cell {
      width: 100vw;
      height: auto;
    }

    video {
      display: block;
      width: 100%;
      height: 100%;
    }

    .progress {
      position: absolute;
      max-width: 1280px;
      width: 90%;
      z-index: 5;
      bottom: 50px;
      text-align: end;
      right: 0;
      left: 0;
      margin: 0 auto;

      .next {
        right: 0;

        .next-btn {
          cursor: pointer;
        }
      }

      .bar-wrap {
        margin-top: 35px;
        background-image: url("../images/progress_bar_bg.png");

        .bar {
          height: 3px;
          background-color: #fff;
        }
      }

      .status {
        margin-top: 10px;
        font-size: 13px;
        color: $light-gray;

        span {
          color: $gray;

        }
      }
    }
  }

  // WE MAKE GAMES BETTER
  .games {
    align-items: center;
    position: relative;
    padding-bottom: 200px;
    margin-top: 80px;

    .games__head {
      text-align: center;

      h1 {
        font-size: 75px;
        font-weight: 300;
        line-height: 1.3;
        margin: 30px 0;

        span {
          font-weight: 700;
        }
      }
    }

    .bar {
      width: 1px;
      height: 40px;
      background-color: black;
      margin: auto;
    }


    .games__body {
      display: flex;
      position: relative;

      .body__text {
        width: 40%;
        font-size: 2.1vw;
        font-weight: 200;
        margin-top: 200px;

        p {
          line-height: 3vw;
          margin-bottom: 2em;
        }
      }

      .body__img {
        position: absolute;
        width: 58%;
        right: -30px;

        img {
          max-width: 100%;
        }
      }
    }

  }

  // WHO WE ARE
  .whoweare {
    position: relative;
    background-image: url('../images/main_mid_bg_02_new.jpg');
    background-position: left center;
    background-size: cover;
    padding-top: 18vw;
    padding-bottom: 20vw;

    h2 {
      font-size: 5vw;
      font-weight: 700;
    }

    .whoweare__content {
      margin-top: 5vw;

      .content__top {
        margin-bottom: 4vw;

        h6 {
          color: $lime;
          font-weight: 700;
          font-size: 18px;
          margin-bottom: 30px;
        }

        h4 {
          display: inline-block;
          font-size: 45px;
          font-weight: 800;
        }

        span {
          color: $gray;
        }
      }

      .content__bottom {

        h3 {
          font-size: 35px;
          font-weight: 800;
          margin-bottom: 30px;
          position: relative;
          width: max-content;

          &::after {
            content: '';
            position: absolute;
            left: 0;
            bottom: 0;
            width: 100%;
            height: 20px;
            background-color: $light-gray;
            z-index: -1;
          }
        }

        p {
          padding-left: 10px;
          font-size: 20px;
          line-height: 1.5;
          font-weight: 200;
        }
      }
    }

    .swiper-slide {
      display: flex;
    }

    .whoweare__img {
      width: 40%;
      display: flex;
      align-items: center;
      justify-content: center;
      margin-top: 50px;

      img {
        width: 20vw;
      }
    }

    .swiper__button {
      position: absolute;
      right: 9vw;
      font-size: 22px;
      font-weight: 700;
      display: flex;
      justify-content: space-between;

      .bar {
        width: 1px;
        height: 10px;
        background-color: $light-gray;
        display: block;
        top: 0;
        bottom: 0;
        margin: auto 20px;
      }

      .swiper-prev,
      .swiper-next {
        font-family: "NEXONLv2Gothic", sans-serif;
        color: $black;
        transition: .2s;
        cursor: pointer;
      }

      .swiper-prev {
        &::before {
          content: '';
          position: absolute;
          left: -30px;
          top: 0;
          bottom: 0;
          margin: auto;
          width: 17px;
          height: 8px;
          background-image: url('https://www.nexongames.co.kr/img/main/slide_prev.png');
        }

        &:hover {
          transform: translateX(-10px)
        }
      }

      .swiper-next {
        &::after {
          content: '';
          position: absolute;
          right: -30px;
          top: 0;
          bottom: 0;
          margin: auto;
          width: 17px;
          height: 8px;
          background-image: url('https://www.nexongames.co.kr/img/main/slide_next.png');
        }

        &:hover {
          transform: translateX(10px);
        }
      }
    }
  }


  // ARTICLE
  .news {
    margin: 100px 0;

    .news-body ul {
      display: flex;
      justify-content: space-between;

      li {
        width: 349px;

        img {
          width: 100%;
        }

        .content {
          margin-top: 40px;

          a {
            font-size: 25px;
            font-weight: 500;
          }

          .content__head {
            margin-bottom: 10px;

            a {
              font-size: 16px;
              font-weight: 500;
              position: relative;
              padding-right: 10px;

              &::after {
                content: '';
                position: absolute;
                width: 1px;
                height: 8px;
                right: 2px;
                background-color: $gray;
                top: 0;
                bottom: 0;
                margin: auto;
              }
            }

            span {
              color: $gray;
              font-weight: 200;
              font-size: 16px;
            }
          }

          .content__body {
            a {
              display: block;
              margin-bottom: 25px;
            }

            p {
              color: $gray;
              font-size: 15px;
              font-weight: 200;
              line-height: 1.3;
            }
          }
        }
      }
    }
  }


  // RECRUITE
  .recruite {
    padding: 100px 0;

    .box-wrapper {
      position: relative;

      img {
        width: 100%;
      }

      .box {
        position: absolute;
        width: 660px;
        height: 170px;
        bottom: -100px;
        left: 0;
        background-color: $white;

        p {
          position: absolute;
          top: 0;
          bottom: 0;
          height: min-content;
          margin: auto;
          font-size: 36px;
          font-weight: 800;
          line-height: 1.2;
        }
      }
    }
  }


  // FOOTER
  footer {
    background-color: #f3f3f3;
    border-bottom: 1px solid #ddd;
    margin-top: 200px;

    .inner {
      padding: 50px 0;
      display: flex;
      justify-content: space-between;

      .footer--left {

        div {
          margin-bottom: 30px;

          a {
            display: inline-block;
          }
        }

        .text {
          color: $gray;
          font-size: 14px;
          font-weight: 200;
          line-height: 1.3;

          p:first-child {
            margin-bottom: 20px;
          }
        }
      }

      .footer--right {
        .row {
          display: flex;

          a {
            display: block;
            padding-bottom: 10px;
            color: $gray;
            font-size: 14px;
            transition: color .1s;

            &:hover {
              color: $black;
            }
          }

          .row--head>a {
            padding-bottom: 20px;
            font-size: 15px;
            color: $black;
          }

          .row--head:not(:last-child) {
            padding-right: 50px;
          }
        }
      }
    }
  }

  .footer-bottom {
    background-color: #f3f3f3;
    padding: 30px 0;

    .inner {
      .wrapper {
        color: $gray;
        font-weight: 200;
        font-size: 15px;
        height: 15px;
        display: flex;
        justify-content: space-between;
        align-items: center;
        position: relative;

        .footer__sites {
          width: 220px;
          font-size: 14px;
          display: flex;
          justify-content: space-between;
          padding: 15px 0;

          &::after {
            content: '▲';
          }
          &:hover {
            .sites__extends {
              display: block;
            }
          }
        }

        .sites__extends {
          display: none;
          position: absolute;
          border: 1px solid;
          bottom: 30px;
          right: 0;
          min-width: 220px;
          z-index: 1;
          background-color: #f1f1f1;
          box-shadow: 0 8px 16px 0 rgba(0,0,0,.2);
          border: none;

          li {
            font-size: 12px;
            cursor: pointer;

            &:hover {
              background-color: #ddd;
            }

            a {
              display: block;
              padding: 12px 16px;
            }
          }
        }
      }
    }
  }


  // TOP BUTTON
  .top-btn {
    width: 59px;
    height: 59px;
    position: fixed;
    bottom: 100px;
    border-radius: 50%;
    background-color: black;
    color: #fff;
    text-align: center;
    right: 40px;
    cursor: pointer;
    line-height: 59px;
    z-index: 10;

    &:hover {
      background-color: #019fe8;
    }
  }
}
